-- ? FUNCTION TO COMMENT / UNCOMMENT
-- ! ###############################
this.Comment = {
	getLines = function( LuaEditor, mode )
		local EmptyText = LuaEditor.SelText == ''
		-- local SelStart = math.min( LuaEditor.SelStart, LuaEditor.SelEnd )
		-- local SelEnd = math.max( LuaEditor.SelStart, LuaEditor.SelEnd )
		local SelStart = LuaEditor.SelStart
		local SelEnd = LuaEditor.SelEnd

		local LineBreak = LuaEditor.Lines.LineBreak -- ! '\r\n'

		local sLine = -1
		local eLine = -1
		local sNum = -1
		local eNum = -1
		local LineNum = 0
		for i = 0, LuaEditor.Lines.Count - 1 do
			LineNum = LineNum + LuaEditor.Lines[i]:len() + 2
			if SelStart <= LineNum and sLine == -1 then
				sLine = LineNum - LuaEditor.Lines[i]:len() - 1
				sNum = i
				if eLine > -1 then
					break
				end
			end
			if SelEnd <= LineNum and eLine == -1 then
				eLine = LineNum
				eNum = i
				if sLine > -1 then
					break
				end
			end
		end
		LuaEditor.SelStart = eLine
		LuaEditor.SelEnd = sLine
		return SelStart, SelEnd, EmptyText, sLine, eLine, sNum, eNum, LineBreak
	end,
	inLine = {
		Comment = function( LuaEditor, mode )
			if mode == 'inLine' then
				-- ! ###############
				-- ? COMMENT IN LINE
				-- ! ###############
				if this.getSettings( this.Features.Comments.Name ) == false then
					return
				end
				local SelStart, SelEnd, EmptyText, sLine, eLine, sNum, eNum, LineBreak = this.Comment.getLines( LuaEditor, mode )
				local offset = 0
				local text = ''
				for i = sNum, eNum do
					offset = offset + 1
					text = text .. '--' .. LuaEditor.Lines[i] .. (i < eNum and LineBreak or '')
				end
				LuaEditor.SelText = text
				LuaEditor.SelStart = eLine + offset * LineBreak:len() + 1
			end
		end,
		Uncomment = function( LuaEditor, mode )
			if mode == 'inLine' then
				-- ! #################
				-- ? UNCOMMENT IN LINE
				-- ! #################
				if this.getSettings( this.Features.Comments.Name ) == false then
					return
				end
				local SelStart, SelEnd, EmptyText, sLine, eLine, sNum, eNum, LineBreak = this.Comment.getLines( LuaEditor, mode )
				local offset = 0
				local text = ''
				local line = ''
				local Found = false
				for i = sNum, eNum do
					offset = offset + 1
					if LuaEditor.Lines[i]:match( '^(%s*%-%-%[)' ) == nil then
						if LuaEditor.Lines[i]:match( '^(%s*%-%-)' ) ~= nil then
							if Found == false then
								Found = true
							end
							line = LuaEditor.Lines[i]:gsub( '^(%s*)%-%-', '%1', 1 ) .. (i < eNum and LineBreak or '')
						else
							line = LuaEditor.Lines[i] .. (i < eNum and LineBreak or '')
						end
						text = text .. line
					end
				end
				if Found then
					LuaEditor.SelText = text
					LuaEditor.SelStart = eLine - offset * LineBreak:len() + 1
				else
					LuaEditor.SelStart = SelEnd
					LuaEditor.SelEnd = SelStart
				end
			end
		end
	},
	inBlock = {
		Comment = function( LuaEditor, mode )
			if mode == 'inBlock' then
				-- ! ################
				-- ? COMMENT IN BLOCK
				-- ! ################
				if this.getSettings( this.Features.Comments.Name ) == false then
					return
				end
				local SelStart, SelEnd, EmptyText, sLine, eLine, sNum, eNum, LineBreak = this.Comment.getLines( LuaEditor, mode )
				local CheckText = LuaEditor.SelText
				while not (CheckText:match( '%-%-%[(=*)%[.-%]%1%]' ) == nil) do
					CheckText = CheckText:gsub( '%-%-%[(=*)%[(.-)%]%1%]', '%2' )
				end
				if not (CheckText:match( '%-%-%[(=*)%[' ) == nil) or not (CheckText:match( '%](=*)%]' ) == nil) then
					if messageDialog( 'UNFINISHED COMMENT BLOCK DETECTED!!!', mtWarning, mbIgnore, mbAbort ) == mrAbort then
						LuaEditor.SelStart = SelEnd
						LuaEditor.SelEnd = SelStart
						return
					end
				end
				local commentEq = LuaEditor.SelText:match( '^.-%-%-%[(=*)%[' ) or ''
				commentEq = '=' .. commentEq
				local offset = 0
				LuaEditor.SelText = '--[' .. commentEq .. '[' .. LuaEditor.SelText .. ']' .. commentEq .. ']'
				LuaEditor.SelStart = eLine + commentEq:len() * 2 + 6 + 1
			end
		end,
		Uncomment = function( LuaEditor, mode )
			if mode == 'inBlock' then
				-- ! ##################
				-- ? UNCOMMENT IN BLOCK
				-- ! ##################
				if this.getSettings( this.Features.Comments.Name ) == false then
					return
				end
				local SelStart, SelEnd, EmptyText, sLine, eLine, sNum, eNum, LineBreak = this.Comment.getLines( LuaEditor, mode )
				local commentEq = LuaEditor.SelText:match( '^.-%-%-%[(=*)%[.-%]%1%]' ) or ''
				if (LuaEditor.SelText:match( '^.-%-%-%[(=*)%[.-%]%1%]' ) == nil) then
					LuaEditor.SelStart = SelEnd
					LuaEditor.SelEnd = SelStart
					-- messageDialog( 'NO COMMENT BLOCK DETECTED???', mtInformation, mbOK )
					return
				end
				LuaEditor.SelText = LuaEditor.SelText:gsub( '^(.-)%-%-%[(=+)%[(.-)%]%2%]', '%1%3' )
				LuaEditor.SelStart = sLine
				LuaEditor.SelEnd = eLine - commentEq:len() * 2 - 6 - 1
			end
		end
	}
}

