		this.Comment.inBlock.Uncomment( LuaEditor, 'inBlock' )
	end, nil, nil, nil, "Shift+" .. this.Features.Comments.inBlockShortcut )
	this.addMenuItem( miEdit, '-' )

	local function selectWord( LuaEditor )
		local safe = 0
		local offset = 0
		local SelStart = LuaEditor.SelStart
		local SelEnd = LuaEditor.SelEnd
		while LuaEditor.SelText:match( "[^%a%x%w']$" ) == nil do
			safe = safe + 1
			LuaEditor.SelEnd = LuaEditor.SelEnd + 1
			if LuaEditor.SelEnd == LuaEditor.Lines.getText():len() - 1 then
				offset = 1
				break
			end

			if LuaEditor.SelText == '' or safe > 50 then
				offset = 1
				break
			end
		end
		LuaEditor.SelEnd = LuaEditor.SelEnd - 1 + offset
		SelEnd = LuaEditor.SelEnd
		offset = 0
		LuaEditor.SelStart = SelStart
		while LuaEditor.SelText:match( "^[^%a%x%w']" ) == nil do
			safe = safe + 1
			LuaEditor.SelStart = LuaEditor.SelStart - 1
			LuaEditor.SelEnd = SelEnd
			if LuaEditor.SelStart == 1 then
				offset = -1
				break
			end
			if LuaEditor.SelText:match( '^' .. LuaEditor.Lines.LineBreak ) then
				offset = 1
				break
			end
		end
		LuaEditor.SelStart = LuaEditor.SelStart + 1 + offset
		LuaEditor.SelEnd = SelEnd
		if LuaEditor.SelText == '' then
			return false
		end
		return true
	end

	local function convertTo( LuaEditor, func )
		local offset = 0
		local len = 0
		local SelStart = LuaEditor.SelStart
		local SelEnd = LuaEditor.SelEnd
		if LuaEditor.SelText == '' then
			if selectWord( LuaEditor ) == false then
				return
			end
		end
		if func == 'miCTupper' then
			LuaEditor.SelText = LuaEditor.SelText:upper()
		elseif func == 'miCTlower' then
			LuaEditor.SelText = LuaEditor.SelText:lower()
		elseif func == 'miCTproper' then
			LuaEditor.SelText = LuaEditor.SelText:gsub( "[']-[^%s%p%c]+", function( s )
				return (s:match( '^(.)' )):upper() .. (s:match( '^.(.*)$' )):lower()
			end )
		elseif func == 'miCTsentence' then
			LuaEditor.SelText = LuaEditor.SelText:gsub( '([^\n.:]+)', function( s )
				return (s:match( '^(%s*%a)' ) and s:match( '^(%s*%a)' ):upper() .. s:match( '^%s*%a(.*)$' ):lower() or s)
			end )
		elseif func == 'miCThex' then
			if LuaEditor.SelText:match( '^%x+$' ) ~= nil then
				len = ('%d'):format( '0x' .. LuaEditor.SelText:match( '^%x+$' ) ):len()
				LuaEditor.SelText = ('%d'):format( '0x' .. LuaEditor.SelText:match( '^%x+$' ) )
				if (SelEnd - SelStart) > 0 then
					offset = len - (SelEnd - SelStart)
				end
			end
		elseif func == 'miCTDec' then
			if LuaEditor.SelText:match( '^%d+$' ) ~= nil then
				len = ('%x'):format( LuaEditor.SelText:match( '^%d+$' ) ):upper():len()
				LuaEditor.SelText = ('%x'):format( LuaEditor.SelText:match( '^%d+$' ) ):upper()
				print( SelEnd - SelStart )
				if (SelEnd - SelStart) > 0 then
					offset = len - (SelEnd - SelStart)
				end
			end
		elseif func == 'miCTstr2bytes' then
			LuaEditor.SelText = LuaEditor.SelText:gsub( '.', function( s )
				return ('%0.2X '):format( s:byte() )
			end )
		elseif func == 'miCTbytes2str' then
			LuaEditor.SelText = LuaEditor.SelText:gsub( '([^%s,]+)[%s,]*', function( s )
				return ('0x' .. s):char()
			end )
		elseif func == 'miCTaline' then
			LuaEditor.SelText = LuaEditor.SelText:gsub( '([^\n%s]+)[\n%s]*', '%1 ' )
		end
